<template>
  <footer class="footer">
    <div class="footer__inner container">
      <img class="footer-logo" :src="common.footer.logo_footer" :alt="common.footer.logo_footer_alt" loading='lazy'>
      <div class="contacts">
        <div class="contacts__item">{{common.contact.city}}, {{common.contact.address}}</div>
        <a class="contacts__item" :href="'tel:' + convertPhone(common.contact.phone)">{{common.contact.phone}} {{common.contact.add_number}}</a>
        <a class="contacts__item" :href="'mailto:' + common.contact.email">{{common.contact.email}}</a>
      </div>

       <nav class="footer-nav">
        <ul class="footer-menu">
          <li class="footer-menu__item" v-for="(item, index) in common.footer.footer_menu" :key="index">
            <NuxtLink class="footer-menu__link" :to="getRootLink() + item.slug">{{item.name}} </NuxtLink>
              <ul class="footer-submenu" v-if="!isEmptyArr(item.submenu)">
                <li class="footer-submenu__item" v-for="(subItem, subindex) in item.submenu" :key="'sub' + subindex">
                  <NuxtLink class="footer-submenu__link" :to="getRootLink() + item.slug + '/' + subItem.slug">{{subItem.name}} </NuxtLink>
                </li>
              </ul>
          </li>
        </ul>
      </nav>

